import React from "react";
import "./digital.css";
import { useState, useEffect } from "react";

// Definici贸n del componente como una funci贸n nombrada
interface tipoHora {
  hora: number;
  minuto: number;
  segundo: number;
  dia: number;
  mes: string;
  anio: number;
}

function ComponenteDigital() {
  const [hora, setHora] = useState<string>("");
  const [fecha, setFecha] = useState<String>("");
  useEffect(() => {
    const actualizarHora = () => {
      const fechaActual = new Date();
      const horaActual = fechaActual.toLocaleTimeString();
      setHora(horaActual);
    };

    // Actualizar la hora cada segundo
    const intervalo = setInterval(actualizarHora, 1000);

    // Limpiar el intervalo cuando el componente se desmonta
    return () => clearInterval(intervalo);
  }, []);

  return (
    <div>
      <section className="horaActual">
        <p className="titulohora">Reloj online</p>\
        <p className="fechaActual">{}</p>
        <p className="horaActual">{hora}</p>
      </section>
    </div>
  );
}

export default ComponenteDigital;
