import React from "react";
import "./analocio.css";
import { useState, useEffect } from "react";
// Definici贸n del componente como una funci贸n nombrada
function ComponenteAnalogico() {

  const [hora, setHora] = useState<string>("");
  const [fecha, setFecha] = useState<String>("");
  const mesesAnios = [
    "Enero",
    "Febrero",
    "Marzo",
    "Abril",
    "Mayo",
    "Junio",
    "Julio",
    "Agosto",
    "Septiembre",
    "Octubre",
    "Noviembre",
    "Diciembre",
  ];
  const diasSemana = [
    "Domingo",
    "Lunes",
    "Martes",
    "Miercoles",
    "Jueves",
    "Viernes",
    "Sabado",
  ];

  const centro = obtenerCentroPantalla();
  useEffect(() => {
    const actualizarHora = () => {
      const fechaActual = new Date();
      const horaActual = fechaActual.toLocaleTimeString();
      const fechaDia = fechaActual.getDay();
      const fechaMes = fechaActual.getMonth();
      const fechaAnio = fechaActual.getFullYear();
      setFecha(
        diasSemana[fechaDia] +
          ", " +
          fechaDia +
          " de " +
          mesesAnios[fechaMes] +
          " de " +
          fechaAnio
      );
      setHora(horaActual);
    };

    const intervalo = setInterval(actualizarHora, 1000);

    return () => clearInterval(intervalo);
  }, []);

  return (
    <div className="">
      <section className="">
        <p className="">Reloj online</p>
        <p className="">{fecha}</p>
        <p className="">{hora}</p>
        <a href="/">inicio</a>
        <svg
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 24 24"
          fill="none"
          stroke="currentColor"
          stroke-width="0.5"
          stroke-linecap="round"
          stroke-linejoin="round"
        >
          <circle cx="12" cy="12" r="5" />
          <line x1="12" y1="8" x2="12" y2="16" />
          <line x1="8" y1="12" x2="16" y2="12" />

        </svg>
      </section>
    </div>
  );
}
const obtenerCentroPantalla = (): { x: number; y: number } => {
  const centroX = window.innerWidth / 2;
  const centroY = window.innerHeight / 2;
  return { x: centroX, y: centroY };
};
export default ComponenteAnalogico;
